  //       </Button>
  //       </Form.Item>
  //     </Form>
  //   )
  // }

  render() {
    const { page, bookList, total } = this.state;
    return (
      <div className="home">
        <Banner />
        <div className="search-background">
          {this.Mohu()}
          {/* <Gaoji /> */}
          {/* <br /><br />
        <Button size="large">切换高级检索</Button> */}
        </div>

        <div className="search-result-box">
          {
            bookList.map(item => {
              const { img, bookName, author, publish, introduce, id } = item;
              return (
                <div className="search-result" key={id} onClick={() => this.toDetail(item)}>
                  <img src={img} alt='' />
                  <div className="search-result-item">
                    <div>题  名：{bookName}</div>
                    <div>作  者：{author}</div>
                    <div>出版社：{publish}</div>
                    <div>简  介：{introduce}</div>
                  </div>
                </div>
              );
            })
          }
        </div>
        <br /><br />
        <Pagination
          showSizeChanger
          onShowSizeChange={this.onShowSizeChange}
          defaultCurrent={page}
          total={total}
        />
      </div>
    );
  }
}
