import React from 'react';
import { Carousel, Input, Row, Col, Radio, Button } from 'antd';
import { SearchOutlined } from '@ant-design/icons';
import { GetPicList, GetBookList } from '../../api'
import Banner from '../../components/Banner';
import './index.css';

const onSearch = value => console.log(value);

const onChange = e => {
  console.log('radio checked', e.target.value);
};

const contentStyle = {
  height: '300px',
  width: '100%',
  overflow: 'hidden'
};

class Home extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      pic: [],
      bookList: [],
    };
  }

  componentDidMount() {
    GetPicList().then(res => {
      this.setState({
        pic: res.data.data,
      })
    });
    GetBookList(1, 5).then(res => {
      this.setState({
        bookList: res.data.data,
      })
    });
  }


  // GetBookList(1, 10).then(res => {
  //   console.log(res);
  // }).catch(err => {
  //   console.log(err);
  // });
  // GetPicList().then(res => {
  //   // let data = res.data;
  //   setPic({...res.data.data});
  //   console.log(res.data);
  // })



  render() {
    const { pic,bookList } = this.state;
    return (
      <div className="home">
        <Banner />
        <Row>
          <Col span={12}>
            <div className="home-searchBox">
              <div className="home-searchBox__title">资源检索</div>
              <Input.Search
                placeholder="请输入模糊检索字段"
                allowClear
                enterButton="检索"
                size="large"
                onSearch={onSearch}
                prefix={<SearchOutlined />}
              />
              <br /><br />
              <Radio.Group onChange={onChange} value='1'>
                <Radio value={1}>全部资源</Radio>
              </Radio.Group>
              <br /><br />
              <div className="home-contents">注：通过资源检索找到对应借阅书目</div>
            </div>
          </Col>
          <Col span={12}>
            <div className="home-carousel">
              {
                pic.length > 0 &&
                <Carousel autoplay>
                  {
                    pic.map(item => {
                      return <div index={item.id}><img style={contentStyle} src={item.url} alt='' /></div>
                    })
                  }
                </Carousel>
              }
            </div>
          </Col>
        </Row>
        <div className="home-newBooks">
          <div className="home-newBooks-title">新书速览</div>
          <div className="home-newBooks-box">

            <div className="home-newBooks-item">
              <img src='http://www.lib.ecnu.edu.cn/_upload/article/images/ff/85/6620697649db94e4a792a575004d/fc82c0c9-bf05-4c46-90fb-00a25d2187b8_s.jpg' alt="" />
              <div>书的名字叫什么</div>
            </div>

          </div>
        </div>
      </div>
    )

  };
}
export default Home;
