import { Button } from 'antd';
import { Link } from 'react-router-dom';
import './index.css';

export default class Banner extends React.Component {
  render() {
    return (
      <div className="banner">
        <Link to='/'><span className="banner-title">高级编程课程图书借阅系统</span></Link>
        <Button className="banner-userName" style={{ display: window.localStorage.userId ? 'none' : 'block' }}>登录</Button>
        {/* <Link to='/my-center'><span className="banner-userName" style={{ display: window.localStorage.userId ? 'block' : 'none' }}>欢迎，{window.localStorage.userId}</span></Link> */}
      </div>
    )
  }
}
