import React from 'react';
import { Carousel, Input, Row, Col, Radio } from 'antd';
import { SearchOutlined } from '@ant-design/icons';
import './index.css';

const Home = () => {
  const [value, setValue] = React.useState(1);
  const img1 = '../../../public/zb1';
  const img2 = 'http://lxs.ecnu.edu.cn/templates/zh/default/images/schollphoto/zb/zb7.jpg';
  const img3 = 'http://lxs.ecnu.edu.cn/templates/zh/default/images/schollphoto/zb/zb7.jpg';
  const img4 = 'http://lxs.ecnu.edu.cn/templates/zh/default/images/schollphoto/zb/zb7.jpg';

  const contentStyle = [
    {
      height: '300px',
      lineHeight: '160px',
      textAlign: 'center',
      backgroundImage: `url("${img1}")`,
      backgroundSize: 'cover',
    },
    {
      height: '300px',
      lineHeight: '160px',
      textAlign: 'center',
      backgroundImage: `url("${img2}")`,
      backgroundSize: 'cover',
    },
    {
      height: '300px',
      lineHeight: '160px',
      textAlign: 'center',
      backgroundImage: `url("${img3}")`,
      backgroundSize: 'cover',
    },
    {
      height: '300px',
      lineHeight: '160px',
      textAlign: 'center',
      backgroundImage: `url("${img4}")`,
      backgroundSize: 'cover',
    },
  ];

  const onSearch = value => console.log(value);

  const onChange = e => {
    console.log('radio checked', e.target.value);
    setValue(e.target.value);
  };

  return (
    <div className="home">
      <Row>
        <Col span={12}>
          <div className="home-searchBox">
            <div className="home-title">资源检索</div>
            <Input.Search
              placeholder="请输入模糊检索字段"
              allowClear
              enterButton="检索"
              size="large"
              onSearch={onSearch}
              prefix={<SearchOutlined />}
            />
            <br /><br />
            <Radio.Group onChange={onChange} value={value}>
              <Radio value={1}>全部资源</Radio>
              <Radio value={2}>小说资源</Radio>
              <Radio value={3}>什么资源</Radio>
            </Radio.Group>
            <br /><br />
            <div className="home-contents">注：通过资源检索找到对应借阅书目</div>
          </div>
        </Col>
        <Col span={12}>
          <div className="home-carousel">
            <Carousel autoplay>
              {
                contentStyle.map((item,index) => {
                  return <div style={contentStyle[index]} key={index}>inh{index}</div>
                })
              }
            </Carousel>
          </div>
        </Col>
      </Row>
    </div>
  );
}
export default Home;
